    private static boolean VerificaReporterCadastro(int id, Reporter ListaR[]) {
        // Verifica se o reporter está previamento cadastrado para poder associ-lo
        for (int y = 0; y < ListaR.length; y++) {
            if (id == ListaR[y].getIdSequecial()) {
                return true;
            }
        }
        return false;
    }
